define hidden i32 @f(ptr %m) {
  %v = load i32, ptr %m, align 4
  ret i32 %v
}

define hidden x86_thiscallcc i32 @g(ptr %m) {
  %v = load i32, ptr %m, align 4
  ret i32 %v
}

define hidden i32 @constexpr_self_user() addrspace(1) {
  %objsize = call i32 @llvm.objectsize.i32.p0(ptr addrspacecast (ptr addrspace(1) @constexpr_self_user to ptr), i1 false, i1 false, i1 false)
  ret i32 %objsize
}

